import React, { useState, useEffect } from "react";

const AdminUserPage = () => {
  const [users, setUsers] = useState([]);

  useEffect(() => {
    // Fetch user data when the component mounts
    fetchUserList();
  }, []);

  const fetchUserList = async () => {
    try {
      const token = sessionStorage.getItem("accessToken");

      // Fetch user list data from the API
      const response = await fetch(
        "https://student360-api.onrender.com/api/user/all",
        {
          headers: {
            Authorization: `${token}`,
          },
        }
      );

      if (!response.ok) {
        throw new Error("Failed to fetch user list");
      }

      const userData = await response.json();
      setUsers(userData.users || []); // Ensure users array is defined or use an empty array
      console.log
    } catch (error) {
      console.error("Error fetching user list:", error);
      alert("Failed to fetch user list. Please try again.");
    }
  };

  return (
    <div className="container mx-auto mt-8">
      <h1 className="text-3xl font-bold mb-8 text-[#3B50FE]">
        Admin User Page
      </h1>

      <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-4 gap-6">
        {users.map((user) => (
          <UserDetail key={user.id} user={user} />
        ))}
      </div>
    </div>
  );
};

const UserDetail = ({ user }) => (
  <div className="bg-black p-6 rounded-md shadow-md">
    <div className="mb-4">
      <strong>Name:</strong> {user.full_name || "Not available"}
    </div>
    <div className="mb-4">
      <strong>Email:</strong> {user.email || "Not available"}
    </div>
    <div className="mb-4">
      <strong>University:</strong> {user.university || "Not available"}
    </div>
    <div className="mb-4">
      <strong>Department:</strong> {user.department || "Not available"}
    </div>
    <div className="mb-4">
      <strong>Level:</strong> {user.level || "Not available"}
    </div>
  </div>
);

export default AdminUserPage;
