import React, { useState, useEffect } from "react";

const AdminUserPage = () => {
  const [users, setUsers] = useState([]);

  useEffect(() => {
    // Fetch user data when the component mounts
    fetchUserList();
  }, []);

  const fetchUserList = async () => {
    try {
      const token = sessionStorage.getItem("accessToken");

      // Fetch user list data from the API
      const response = await fetch(
        "https://student360-api.onrender.com/api/user/all",
        {
          headers: {
            Authorization: `${token}`,
          },
        }
      );

      if (!response.ok) {
        throw new Error("Failed to fetch user list");
      }

      const userData = await response.json([]);
      setUsers(userData['All users'] || []); // Ensure users array is defined or use an empty array
      console.log(userData);
    } catch (error) {
      console.error("Error fetching user list:", error);
      alert("Failed to fetch user list. Please try again.");
    }
  };

  return (
    <div className="container mx-auto mt-8">
      <h1 className="text-3xl font-bold mb-8 text-[#3B50FE]">
        Admin User Page
      </h1>

      <div className="mb-4">
        <strong>Name</strong> | <strong>Email</strong> | <strong>University</strong> | <strong>Department</strong> | <strong>Level</strong>
      </div>

      {users.map((user) => (
        <UserDetail key={user.id} user={user} />
      ))}
    </div>
  );
};

const UserDetail = ({ user }) => (
  <div className="bg-white p-6 rounded-md shadow-md mb-4">
    <div>
      {user.full_name || "Not available"} | {user.email || "Not available"} | {user.university || "Not available"} | {user.department || "Not available"} | {user.level || "Not available"}
    </div>
  </div>
);

export default AdminUserPage;
